function initState(){
    return {
        totalQuantity: localStorage.getItem("localQuantity"),
    }
}

export const cart = {
    state: initState,
    mutations : {
        addCart(state,productCount){       
            state.totalQuantity += productCount;
            localStorage.setItem("localQuantity",state.totalQuantity)
    }    
},
    actions :{
        addCart(commit,productCount){
            commit.addCart("addCart",productCount)
        }
    },
    getters:{
        getTotalQuantity: state => state.totalQuantity

    }
}
