function initState(){
    return {
        totalQuantity: localStorage.getItem("localQuantity") || 0,
        productsIncart: JSON.parse(localStorage.getItem("productsIncart")) || []
    }
}

export const cart = {
    state: initState,
    mutations : {
        addCart(state,product){       
            // 장바구니 수량
            state.totalQuantity = parseInt(state.totalQuantity) + product.productCount;
            localStorage.setItem("localQuantity",state.totalQuantity)

            // 장바구니 안에 상품목록
            state.productsIncart
    }    
},
    actions :{
        addCart(commit,productCount){
            commit.addCart("addCart",productCount)
        }
    },
    getters:{
        getTotalQuantity: state => state.totalQuantity

    }
}
