<template>
  <v-container>
    <v-row>
      <v-col>
        <v-card>
          <v-card-title>회원 상세 정보</v-card-title>
          <v-card-text>
            <v-row>
              <v-col cols="12" md="6">
                <v-text-field
                  label="ID"
                  :value="member.id"
                  readonly
                />
              </v-col>
              <v-col cols="12" md="6">
                <v-text-field
                  label="이름"
                  :value="member.name"
                  readonly
                />
              </v-col>
              <v-col cols="12" md="6">
                <v-text-field
                  label="이메일"
                  :value="member.email"
                  readonly
                />
              </v-col>
              <v-col cols="12" md="6">
                <v-text-field
                  label="권한"
                  :value="member.role"
                  readonly
                />
              </v-col>
            </v-row>
          </v-card-text>
          <v-card-actions>
            <v-btn @click="goBack">목록으로</v-btn>
          </v-card-actions>
        </v-card>
      </v-col>
    </v-row>
  </v-container>
</template>

<script>
import axios from 'axios';
import { getResultData, getErrorMessage } from '@/utils/commonDataHandler';
import { jwtDecode } from 'jwt-decode';

export default {
  props: {
    isMyPage: {
      type: Boolean,
      default: false
    }
  },
  data() {
    return {
      member: {
        id: '',
        name: '',
        email: '',
        role: ''
      }
    };
  },
  async created() {
    try {
      const accessToken = localStorage.getItem('accessToken');
      if (!accessToken) {
        alert('로그인이 필요합니다.');
        this.$router.push('/member/login');
        return;
      }

      const payload = jwtDecode(accessToken);
      if (this.isMyPage && payload.id) {
        // 마이페이지인 경우: 현재 로그인한 사용자의 ID로 조회
        await this.getMemberDetail(payload.id);
      } else {
        // 관리자 페이지인 경우: URL 파라미터에서 ID 사용
        await this.getMemberDetail();
      }
    } catch (error) {
      console.error('초기화 오류:', error);
      alert('사용자 정보를 불러오는 데 실패했습니다.');
      this.$router.push('/member/login');
    }
  },
  methods: {
    async getMemberDetail(memberId = this.$route.params.id) {
      try {
        const accessToken = localStorage.getItem('accessToken');
        const response = await axios.get(
          `${process.env.VUE_APP_API_BASE_URL}/member/detail/${memberId}`,
          {
            headers: {
              Authorization: `Bearer ${accessToken}`
            }
          }
        );
        this.member = getResultData(response);
      } catch (error) {
        if (error.response?.status === 401) {
          alert('관리자 권한이 필요합니다.');
          this.$router.push('/member/login');
        } else if (error.response?.status === 403) {
          alert('접근 권한이 없습니다.');
          this.$router.push('/member/list');
        } else {
          alert(getErrorMessage(error));
        }
      }
    },
    goBack() {
      this.$router.push(this.isMyPage ? '/member/mypage' : '/member/list');
    }
  }
};
</script>
