import axios from 'axios';
export default {
  data() {
    return {
      name: "",
      email: "",
      password: ""
    };
  },
  methods: {
    async memberCreate() {
      try {
        const data = {
          name: this.name,
          email: this.email,
          password: this.password
        };
        await axios.post("http://localhost:8080/member/create", data);
        // 에러가 터지는 경우 e변수 안에 서버에서 주는 error
      } catch (e) {
        console.log(e);
        alert(e.response.data.status_message);
      }
    }
  }
};
