function initState(){
    return {
        count:0
    }
}

// 상태 유호 범위와 리셋 시점
// 리셋시점 : 페이지 새로고침, 브라우저 종료시
// 유효시점 : SPA라우팅시
export const practice = {
    namespaced: true,  // ✅ 네임스페이스 추가
    // 배열형식 아닌 객체 형식
    // 상태 초기화
    state: initState,
    // 상태값을 변경하는 매서드
    mutations : {
        incrementCount(state){
            state.count++;
        }

    },
    // 컴포넌트에서 mutation을 직접 호출한다기 보다는, actions 를 통해 mutation을 호출
    actions :{
        incrementCount({commit}){
            // vuex에서 action에서 mutations 를 호출할떄에는 commit함수를 사용
            // 이와 같은 구조는 mutations의 조립을 actioms에서 호출할수있기 떄문
            commit('incrementCount')
        }


    },
    // 상태값을 가져가기 위한 매서드
    getters : {
        getCount: state => state.count
    }
}
