<template>
  <v-container>
    <v-row>
      <v-col>
        <v-card>
          <v-card-title>회원 상세 정보</v-card-title>
          <v-card-text>
            <v-row>
              <v-col cols="12" md="6">
                <v-text-field
                  label="ID"
                  :value="member.id"
                  readonly
                />
              </v-col>
              <v-col cols="12" md="6">
                <v-text-field
                  label="이름"
                  :value="member.name"
                  readonly
                />
              </v-col>
              <v-col cols="12" md="6">
                <v-text-field
                  label="이메일"
                  :value="member.email"
                  readonly
                />
              </v-col>
              <v-col cols="12" md="6">
                <v-text-field
                  label="권한"
                  :value="member.role"
                  readonly
                />
              </v-col>
            </v-row>
          </v-card-text>
          <v-card-actions>
            <v-btn @click="goBack">목록으로</v-btn>
          </v-card-actions>
        </v-card>
      </v-col>
    </v-row>
  </v-container>
</template>

<script>
import axios from 'axios';
import { getResultData, getErrorMessage } from '@/utils/commonDataHandler';

export default {
    data() {
        return {
            member: {
                id: '',
                name: '',
                email: '',
                role: ''
            }
        }
    },
    async created() {
        // 🔥 안전한 파라미터 체크 추가
        const memberId = this.$route.params.id;

        console.log('🔍 디버깅:', {
            route: this.$route,
            params: this.$route.params,
            memberId: memberId,
            memberIdType: typeof memberId
        });

        if (!memberId || memberId === 'undefined') {
            console.error('❌ 올바르지 않은 회원 ID:', memberId);
            alert('올바르지 않은 회원 ID입니다.');
            this.$router.push('/member/list');
            return;
        }

        await this.getMemberDetail();
    },
    methods: {
        async getMemberDetail() {
            try {
                const accessToken = localStorage.getItem('accessToken');
                const memberId = this.$route.params.id;

                // 🔥 다시 한번 체크
                if (!memberId || memberId === 'undefined') {
                    throw new Error('회원 ID가 유효하지 않습니다.');
                }

                console.log('📤 API 요청 URL:', `${process.env.VUE_APP_API_BASE_URL}/member/detail/${memberId}`);

                const response = await axios.get(
                    `${process.env.VUE_APP_API_BASE_URL}/member/detail/${memberId}`,
                    {
                        headers: {
                            Authorization: `Bearer ${accessToken}`
                        }
                    }
                );

                this.member = getResultData(response);
                console.log('✅ 회원 정보 로드 성공:', this.member);

            } catch (error) {
                console.error('❌ getMemberDetail 오류:', error);

                if (error.response?.status === 401) {
                    alert('관리자 권한이 필요합니다.');
                    this.$router.push('/member/login');
                } else if (error.response?.status === 403) {
                    alert('접근 권한이 없습니다.');
                    this.$router.push('/member/list');
                } else {
                    alert(getErrorMessage(error));
                    this.$router.push('/member/list');
                }
            }
        },
        goBack() {
            this.$router.push('/member/list');
        }
    }
}
</script>
