<template>
  <v-container>
    <v-row>
      <v-col>
        <v-card>
          <v-card-title>주문 목록</v-card-title>
          <v-card-text>
            <v-data-table
              :headers="tableHeader"
              :items="orderList"
              show-expand
              item-value="id"
            >
              <!-- Expanded row for order details -->
              <template v-slot:expanded-item="{ headers, item }">
                <tr>
                  <td :colspan="headers.length">
                    <v-icon size="small">mdi-package</v-icon>
                    <strong>상세 내역:</strong>
                    <span v-for="detail in item.orderDetails" :key="detail.detailId">
                      {{ detail.productName }} {{ detail.productCount }}개,
                    </span>
                  </td>
                </tr>
              </template>
            </v-data-table>
          </v-card-text>
        </v-card>
      </v-col>
    </v-row>
  </v-container>
</template>

<script>
import axios from 'axios';
import { getResultData, getErrorMessage } from '@/utils/commonDataHandler';

export default {
    props: ['isMyPage'],
  data() {
    return {
      tableHeader: [
        { title: 'ID', key: 'id' },
        { title: '회원이름', key: 'member.name' },
        { title: '회원 Email', key: 'member.email' }
      ],
      orderList: []
    };
  },
  async created() {
    try {
      const accessToken = localStorage.getItem('accessToken');
      if (!accessToken) {
        alert('로그인이 필요합니다.');
        this.$router.push('/member/login');
        return;
      }

      const response = await axios.get(
        `${process.env.VUE_APP_API_BASE_URL}/ordering/list`,
        {
          headers: {
            Authorization: `Bearer ${accessToken}`
          }
        }
      );
      this.orderList = getResultData(response) || [];
    } catch (error) {
      console.error('주문 목록 조회 오류:', error);
      alert(getErrorMessage(error));
      if (error.response?.status === 401) {
        this.$router.push('/member/login');
      }
    }
  }
};
</script>
